import { useEffect, useState } from "react";
import "./Add.css";
import { dataService } from "../../../Services/DataService";
import { FurnitureTypeModel } from "../../../Models/FurnitureTypeModel";
import { FurnitureModel } from "../../../Models/FurnitureModel";
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import { notify } from "../../../Utils/Notify";

function Add(): JSX.Element {

    const [types, setTypes] = useState<FurnitureTypeModel[]>([]);
    const { register, handleSubmit } = useForm<FurnitureModel>();
    const navigate = useNavigate();

    useEffect(() => {
        dataService.getAllFurnitureType()
          .then((types) => setTypes(types))
          .catch((err) => notify.error(err));
      }, []);

      async function send(furniture:FurnitureModel) {
        try {
          await dataService.addFurniture(furniture);
          console.log (furniture.type);
          notify.success("Item was added");
          navigate("/list");
        } catch (err: any) {
          notify.error(err);
        }
      }

    return (
        <div className="Add">

            <h2>Add Furniture</h2>


            <form onSubmit={handleSubmit(send)}>
        <label>Furniture Type:</label>
        <select defaultValue="" onChange={con} {...register("type")} required>
          <option disabled value="">
            Select Type
          </option>
          {types.map((a) => (
            <option key={a.id} value={a.id}>
              {a.name}
            </option>
          ))}
        </select>

        <label>Size</label>
        <input type="text"{...register("size") }required />
        <label>Color</label>
        <input type="text"{...register("color")}required />
        <label>Price</label>
        <input type="number"{...register("price")}required min="0"/>



        <button>Add </button>


      </form> 

        </div>
    );

}


export default Add;
